			if (otherTrxView != null && otherTrxView.size() > 0) {
				otherTrxView.forEach(trx -> {
					Double amt = trx.getAmount();
					String fullDesc = trx.getType().equals("D")
							? Constants.DEPOSIT_DESC + Constants.SPACE + Constants.DOLLAR_SIGN
