               return event;
            }
            // do nothing if this is not our acquisition
            if (acqIndex != null
                  && event.getTags().containsKey(ACQ_IDENTIFIER)
                  && !(Integer.valueOf(event.getTags().get(ACQ_IDENTIFIER)).equals(acqIndex))) {
