                case COLLISION_ELASTIC:
                    aPdu = new CollisionElasticPdu();
                    break;
                case ENTITY_STATE_UPDATE:
                    aPdu = new EntityStateUpdatePdu();
                    break;
                default:
                    this.logger.log(Level.INFO, "PDU not implemented. Type = " + pduType + "\n");
