                        this.logger.log(Level.INFO, "  PDU  name is: " + pduTypeEnum.getDescription());
                    }
            }

            if (aPdu != null) {
                ((Pdu) aPdu).unmarshal(buff);
            }

            return (Pdu) aPdu;
