                    selectThirdPartyAPIService(
                            configurationService.getDvaDirectEnabled(),
                            configurationService.getDvlaDirectEnabled(),
                            headers.get(HEADER_DOCUMENT_CHECKING_ROUTE),
                            drivingPermitFormData.getLicenceIssuer());

