            auditService.sendAuditEvent(
                    AuditEventType.RESPONSE_RECEIVED, new AuditEventContext(headers, sessionItem));

            LOGGER.info("Sending audit event REQUEST_SENT...");
            auditService.sendAuditEvent(
                    AuditEventType.REQUEST_SENT,
