                int dayDifference;
                if ((dayDifference = a.getDate().getDay()-b.getDate().getDay())==0) {
                    return a.getName().compareToIgnoreCase(b.getName());
                } else {
                    return dayDifference;
                }
            } else {
                return monthDifference;
            }
        } else {
            return yearDifference;
        }
